mapper.select:
select destinationNum, destinationTitle, destinationFirstImg, destinationAddress, destinationSecondImg, destinationContent from( select /*+ INDEX(destination indexNum)*/ rownum rn, destinationNum, destinationTitle, destinationFirstImg, destinationAddress, destinationSecondImg, destinationContent from destination
where
ROWNUM <= #{pageNum}*#{amount}) where rn > (#{pageNum}-1)*#{amount}

mapper.update:
update destination
set destinationTitle = #{destinationTitle},
destinationAddress = #{destinationAddress},
destinationFirstImg = #{destinationFirstImg},
destinationContent = #{destinationContent}
where destinationNum = #{destinationNum}
