mapper.select:

	select num, title, address, firstimg, tel
from(
	select /*+ INDEX(destination_impl indexNum)*/
	rownum rn, num, title, address, firstimg, tel
	from destination_impl
where



(address like '%'|| #{address} ||'%'



) and ROWNUM <= #{pageNum}* #{amount})
where
rn > (#{pageNum}-1)*#{amount}
